import React, { useEffect, useState } from 'react'
import Carousel from '../components/Carousel'
import Navbar from '../components/Navbar'
import Cards from '../components/Cards'
import FarmerCard from '../components/FarmerCard'
import axios from 'axios'
import Footer from '../components/Footer'

export default function Home() {


  const [foodcat, setfoodcat] = useState([]);
  const [fooditem, setfooditem] = useState([]);
  const [farmer, setfarmer] = useState([]);

  const loadData = async () => {
    let response = axios.post("http://localhost:5001/api/fooddata", {

    }).then((response) => {
      setfooditem(response.data[0]);
      setfoodcat(response.data[1]);
      setfarmer(response.data[2]);
    })

  }

  useEffect(() => {
    loadData()
  }, [])

  return (
    <div>
    <div style={{"overflowX":"clip"}}>
      <Navbar />
      <Carousel />
      <div className='container'>
        {
          foodcat.map((data) => {
            return (<div className='row mb-60'>
              <div key={data._id} className="fs-2">
                {data.CategoryName}
              </div>
              {
                fooditem.filter((item) => item.CategoryName == data.CategoryName).map(filteritems => {
                  return (
                    <div className='col-12 col-md-6 col-lg-3'>
                      <Cards img={filteritems.img} title={filteritems.ProductName} quantity={filteritems.Quantity} price={filteritems.Price} />
                    </div>
                  )
                })
              }
            </div>
            )
          })
        }
      </div>
      <div className='row'>
        <p className='fs-2 mb-3'>Connect to your Trustable Farmer</p>
        {
          farmer.map((farmerdata) => {
            return (
              
              <div className='col-12 col-md-6 col-lg-3'>
              <FarmerCard title={farmerdata.Name} img={farmerdata.img} Location={farmerdata.Location} sold={farmerdata.products[0].ProductName}/>
              
              </div>
            )
          })

          // farmer.map((farmerdata) => {
          //   return (
          //     <div className='mt-20'>
          //       <h2>Connect to your Trustable Farmer</h2>
          //       <div className='col-12 col-md-6 col-md-3'>
          //         <FarmerCard title={farmerdata.Name} Location={farmerdata.Location} sold={farmerdata.product[0].ProductName} />
          //       </div>
          //     </div>
          //   )
        }

      </div>
    </div>
    <Footer/>
    </div>
  )

}
