import React from 'react'
import { useState } from 'react';
export default function Cartcomponent(props) {

    const [quantity, setquantity] = useState(1);

    function incquantity(){
        setquantity(quantity+1);
    }

    function decquantity(){
        if(quantity>0)
        setquantity(quantity-1);
    }
    let netprice = props.price*quantity;
    return (
        <div>
            <div style={{"border":"2px solid black"}} class="card rounded-3 mb-4">
                <div class="card-body p-4">
                    <div class="row d-flex justify-content-between align-items-center">
                        <div class="col-md-2 col-lg-2 col-xl-2">
                            <img style={{"border":"2px solid black"}}
                                src={props.img}
                                class="img-fluid rounded-3" alt="Fruit" />
                        </div>
                        <div class="col-md-3 col-lg-3 col-xl-3">
                            <p class="fs-3 lead fw-normal mb-2">{props.title}</p>
                            {/* <p><span class="text-muted">Size: </span>M <span class="text-muted">Color: </span>Grey</p> */}
                        </div>
                        <div class="col-md-3 col-lg-3 col-xl-2 d-flex">
                            <button class="btn btn-link px-2"
                                onClick={decquantity}>
                                <i class="fas fa-minus"></i>
                            </button>

                            <input id="form1" min="0" name="quantity" value={quantity} type="number"
                                class="form-control form-control-sm" />

                            <button class="btn btn-link px-2"
                                onClick={incquantity}>
                                <i class="fas fa-plus"></i>
                            </button>
                        </div>
                        <div class="col-md-3 col-lg-2 col-xl-2 offset-lg-1 mr-32">
                            <h2 class="mb-0 fs-5"> Price :  <span className='fw-bold'>{netprice} Rs</span></h2>
                        </div>
                        {/* <div class="col-md-1 col-lg-1 col-xl-1 text-end">
                            <input type="button" onClick={()=>deleteItem(item)} value='delete' class="text-danger" ><i class="fas fa-trash fa-lg"></i></input>
                        </div> */}

                        {/* <input type="button" className='col-md-1 col-lg-1 col-xl-1 text-end'/> */}
                    </div>
                </div>
            </div>
        </div>
    )
}
